class Solution {
public:
    void int_bin(int n,vector<int> &bits)
    {
        int x = n;
        for(int i=0;i<31;i++)
        {
            bits[i] += (x&1);
            x = x>>1;
            if(x==0) break;
        }
    }
    bool checker(int &base,int &k,vector<int> &bits,int d)
    {
        int x = 0;
        for(int i=0;i<31;i++)
        {
            if(bits[i] == d)
                x += 1<<i;
        }
        base = min(base,abs(k-x));
        if(x < k) return true;
        return false;
    }
    int minimumDifference(vector<int>& nums, int k) 
    {
        int x,n = nums.size(),base = INT_MAX;
        for(int i=0;i<n;i++)
        {
            base = min(base,abs(nums[i]-k));
        }
        vector<int> bits(31,0);
        int left=0,right=0;
        while(right<n)
        {
            x = nums[right];
            int_bin(nums[right],bits);
            bool check = true;
            while(check)
            {
                check = checker(base,k,bits,right-left+1);
                if(check)
                {
                    x = nums[left];
                    for(int i=0;i<31;i++)
                    {
                        bits[i] -= (x&1);
                        x = x>>1;
                        if(x==0) break;
                    }
                    left++;
                    if(right==left) break;
                }
            }
            right++;
        }
        return base;
    }
};
