struct sparse_table {
  vector<vector<int>> dp;
  int k = 0;
  sparse_table(int n, vector<int> &v) : k(__lg(n)) {
    dp.resize(k + 1, vector<int>(n));
    ranges::copy(v, dp[0].begin());
    for (int i = 1; i <= k; i++)
      for (int j = 0; j + (1 << i) <= n; j++) {
        auto a = dp[i - 1][j];
        auto b = dp[i - 1][j + (1 << (i - 1))];
        dp[i][j] = a & b;
      }
  }
  int query_fast(int l, int r) { // only use if operation is idempotent!
    int i = __lg(r - l + 1);
    auto a = dp[i][l];
    auto b = dp[i][r - (1 << i) + 1];
    return a & b;
  }
};

template <typename T, typename U>
T last_true(T lo, T hi, U f) {
  --lo;
  while (lo < hi) {
    T mid = lo + (hi - lo + 1) / 2;
    f(mid) ? lo = mid : hi = mid - 1;
  }
  return lo;
}

class Solution {
public:
  int minimumDifference(vector<int> &a, int k) {
    int n = a.size();
    sparse_table sp(n, a);
    int ans = 1e9;
    for (int i = 0; i < n; i++) {
      int j = last_true(i, n - 1, [&](int m) {
        return sp.query_fast(i, m) >= k;
      });
      if (j == i - 1) {
        ans = min(ans, k - a[i]);
      } else {
        ans = min(ans, sp.query_fast(i, j) - k);
        if (j + 1 < n) {
          ans = min(ans, k - sp.query_fast(i, j + 1));
        }
      }
    }
    return ans;
  }
};
