class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {

        int n=nums.size();



        map<int,int>mp,mp1;

        int l=0,r=0;
        int ans=INT_MAX;
        int val=nums[0];
         for(int i=0;i<n;i++){

            ans=min(ans,abs(nums[i]-k));
        }

        while(r<n){

            val=val&nums[r];

            for(int i=31;i>=0;i--){

                if(nums[r]&(1<<i)){
                    mp[i]++;
                }else{
                    mp1[i]++;
                }
            }

            ans=min(ans,abs(k-val));

            while( l<r && val<k){

                      for(int i=31;i>=0;i--){

                if(nums[l]&(1<<i)){
                    mp[i]--;
                }else{
                    mp1[i]--;
                    if(mp1[i]==0){

                        if(mp[i]>0){
                            val=val|(1<<i);
                        }
                    }
                }
            }

                ans=min(ans,abs(k-val));

                l++;







            }

            ans=min(ans,abs(k-val));


            r++;




        }
      return ans;
    }
};
