class SegmentTree {
private:
    int n;
    std::vector<int> tree;

    void buildTree(const std::vector<int>& nums, int idx, int start, int end) {
        if (start == end) {
            tree[idx] = nums[start];
            return;
        }

        int mid = (start + end) / 2;
        buildTree(nums, 2 * idx + 1, start, mid);
        buildTree(nums, 2 * idx + 2, mid + 1, end);

        tree[idx] = tree[2 * idx + 1] & tree[2 * idx + 2];
    }

    int _query(int idx, int start, int end, int left, int right) {
        if (start >= left && end <= right) {
            return tree[idx];
        }

        if (start > right || end < left) {
            return std::numeric_limits<int>::max();
        }

        int mid = (start + end) / 2;
        int left_child = _query(2 * idx + 1, start, mid, left, right);
        int right_child = _query(2 * idx + 2, mid + 1, end, left, right);

        return left_child & right_child;
    }

public:
    SegmentTree(const std::vector<int>& nums) {
        n = nums.size();
        tree.resize(4 * n);
        buildTree(nums, 0, 0, n - 1);
    }

    int query(int left, int right) {
        return _query(0, 0, n - 1, left, right);
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        SegmentTree st(nums);
        int min_abs_diff = std::numeric_limits<int>::max();

        for (int index = 0; index < n; ++index) {
            int l = index - 1, r = n - 1;
            while (r - l > 1) {
                int mid = (l + r) / 2;
                if (st.query(index, mid) >= k) {
                    l = mid;
                } else {
                    r = mid;
                }
            }
            if (l != index - 1) {
                min_abs_diff = std::min(min_abs_diff, abs(st.query(index, l) - k));
            }
            min_abs_diff = std::min(min_abs_diff, abs(k - st.query(index, r)));
        }

        return min_abs_diff;
    }
};
