class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();

        vector<vector<int>> fq(n+1, vector<int> (31, 0));

        for(int i=0; i<n; i++)
        {
            for(int j=0; j<31; j++)
            {
                if(nums[i]&(1<<j)) fq[i+1][j]=fq[i][j]+1;
            }
        }

        int ans=INT_MAX;

        for(int i=0; i<n; i++)
        {
            int l=i, r=n-1;
            while(l<=r)
            {
                int m=l+(r-l)/2;
                int val=0;
                for(int j=0; j<31; j++)
                {
                    int count=fq[m+1][j]-fq[i][j];
                    if(count==m+1-i) val|=(1<<j);
                }

                ans=min(ans, abs(val-k));

                if(val>k) l=m+1;
                else if(val<k) r=m-1;
                else return 0;
            }
        }

        return ans;
    }
};
