class Solution {
public:
    vector<vector<int>> st;
    int minimumDifference(vector<int>& a, int k) {
        int ret = INF;
        N = sz(a);
        st = vector<vector<int>> (N, vector<int>(18));
        for(int i = 0; i < N; i++) {
            st[i][0] = a[i];
        }
        for(int i = 1; i < 18; i++) {
            for(int j = 0; j < N; j++) {
                if(j+(1<<(i-1)) < N) {
                    st[j][i] = st[j][i-1] & st[j+(1<<(i-1))][i-1];
                }
            }
        }

        for(int i = 0; i < N; i++) {
            if(a[i] < k && k - a[i] > ret) continue;
            ret = min(ret, f(i, k, a));
        }
        return ret;
    }
    // i로부터 시작하는 subarray중 최적해
    // 구간이 증가함에 따라 값은 감소한다.
    // k이상인, 가장 작은 값을 찾아야 한다.
    int f(int i, int k, vector<int> &a) {
        if(a[i] <= k) return abs(k - a[i]);
        int l = i, r = N-1, res = 0;
        while(l <= r) {
            int m = (l+r)/2;
            int cand = g(i,m);
            if(cand >= k) {
                res = m;
                l = m+1;
            } else r = m-1;
        }
        int ret = abs(k - g(i, res));
        if(res+1 < N) ret = min(ret, abs(k - g(i, res+1)));
        return ret;
    }
    int g(int l, int r) {
        if(l==r) return st[l][0];
        auto x = bit_width(bit_floor(unsigned(r-l+1)))-1;
        return st[l][x] & st[r - bit_floor(unsigned(r-l+1))+1][x];
    }
};
