class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {

        set<int>st;
        set<int>prev;
        // prev.insert((1<<30)-1);
        for(auto it:nums){
            set<int>st2;
            st2.insert(it);
            st.insert(it);
            for(auto i:prev){
                st2.insert((it & i));
                st.insert((it&i));
            }
            prev = st2;
        }  

        int ans = 2e9;

        for(auto i:st) ans = min(ans,abs(i - k));

        return ans;
    }
};
