class Solution {
public:
    void substract(vector<int>& zero, int x) {
        for (int i = 0; i < 32; ++i) {
            if (((x >> i) & 1) == 0) {
                zero[i] -= 1;
            }
        }
    }

    void addition(vector<int>& zero, int x) {
        for (int i = 0; i < 32; ++i) {
            if (((x >> i) & 1) == 0) {
                zero[i] += 1;
            }
        }
    }

    int findSolution(vector<int>& zero) {
        int res = 0;
        for (int i = 0; i < 32; ++i) {
            if (zero[i] == 0) {
                res ^= (1 << i);
            }
        }
        return res;
    }

    int minimumDifference(vector<int>& nums, int right) {
        vector<int> zero(32, 0);

        int i = 0;
        int n = nums.size();
        int ans = INT_MAX;

        for (int left = 0; left < n; ++left) {
            addition(zero, nums[left]);
            int x = findSolution(zero);
            ans = min(ans, abs(x - right));
            while (i < left && x < right) {
                substract(zero, nums[i]);
                ++i;
                x = findSolution(zero);
                ans = min(ans, abs(x - right));
            }
        }

        return ans;
    }
};
