class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size(), MAXLEVEL = 30, x, i, j, ans = abs(nums[0] - k), minn, curr;
        VVI next(n + 1, VI(MAXLEVEL, n));
        for (i = n - 1; i >= 0; i--) {
            next[i] = next[i + 1];
            FOR(j, MAXLEVEL) {
                if (!((1 << j) & (nums[i]))) next[i][j] = i;
            }
        }

        FOR(i, n) {
            x = nums[i]; curr = i + 1;
            ans = min(ans, abs(x - k));
            while (curr < n) {
                minn = n;
                FOR(j, MAXLEVEL) {
                    if (!((1 << j) & x)) continue;
                    minn = min(minn, next[curr][j]);
                }

                FOR(j, MAXLEVEL) {
                    if (!((1 << j) & x)) continue;
                    if (minn == next[curr][j]) x ^= (1 << j);
                }
                if (minn < n) ans = min(ans, abs(x - k));
                curr = minn + 1;
            }
        }

        return ans;
    }
};
