class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        unordered_set<int> s[2];
        int c = 0;
        int p = 1;
        int ans = INT_MAX;

        for (auto n: nums) {
            if (n == k)
                return 0;

            s[c].insert(n);
            ans = min(ans, abs(n - k));

            for (auto i: s[p]) {
                if ((i & n) == k)
                    return 0;
                ans = min(ans, abs((i & n) - k));
                s[c].insert(i & n);
            }

            s[p].clear();
            swap(c, p);
        }

        return ans;
    }
};
