class Solution {
public:
    int minimumDifference(vector<int>& v, int k) {
        int ans = 1e9;
        int n = v.size();
        vector<vector<int>>pref(n,vector<int>(32));
        for(int i = 0;i<n;i++){
            ans = min(ans,abs(k-v[i]));

            for(int j = 0;j<32;j++){
                if(i!=0)pref[i][j] = pref[i-1][j];
                if(v[i]&(1<<j)){
                    pref[i][j] += 1;
                }
            }
        }


        for(int i = 1;i<n;i++){
            int st = 0,en = i;
            while(st<=en){
                int md = (st+en)/2;
                int len = i - md + 1;
                int an = 0;
                for(int j = 0;j<32;j++){
                    int cnt = pref[i][j] - (md == 0 ? 0 : pref[md-1][j]);
                    if(cnt == len)an += (1<<j);
                }
                if(an>=k){

                    en = md - 1;
                }else{
                    st = md + 1;
                }
                ans = min(ans,abs(k-an)); 
            }
        }
        return ans;




    }
};
