class Solution {
public:
    void add(vector<int> &freqOne, int x)
    {
        for(int i=0; i<32 && x>0; i++)
        {
            freqOne[i] += (x&1);
            x>>=1;
        }
        return;
    }
    void remove(vector<int> &freqOne, int x)
    {
        for(int i=0; i<32 && x>0; i++)
        {
            freqOne[i] -= (x&1);
            x>>=1;
        }
        return;
    }
    int check(vector<int> &freqOne, int target)
    {
        int res = 0;
        for(int i=0; i<32; i++)
        {
            if(freqOne[i]==target)
                res+= (1<<i);
        }
        return res;
    }
    int minimumDifference(vector<int>& nums, int k) {

        vector<int> freqOne (32, 0);
        int res = INT_MAX, n = nums.size();
        for(int i=0, j=0; j<n; j++)
        {
            add(freqOne, nums[j]);
            int r = check(freqOne, j-i+1);
            res = min(res, abs(r-k));

            while(i<=j && r<k)
            {
                remove(freqOne, nums[i]);
                r = check(freqOne, j-i);
                res = min(res, abs(r-k));
                i++;
            }
        }
        return res;
    }
};

