class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>>dp(31,vector<int>(n+1,0));
        for(int i=30;i>=0;i--){
            long long int p=(1ll<<i);
            for(int j=1;j<=n;j++){
                dp[i][j]+=dp[i][j-1];
                if(p&nums[j-1]){
                    dp[i][j]++;
                }
            }
        }
        // for(int i=30;i>=0;i--){
        //     for(int j=0;j<=n;j++)
        //         cout<<dp[i][j]<<" ";
        //     cout<<endl;
        // }
        int res=1e9;
        for(int i=1;i<=n;i++){
            int low=1, high=i;
            int ans1=-1;
            while(low<=high){
                int mid=(low+high)/2;
                int len=i-mid+1;
                long long int currans=0;
                for(int j=30;j>=0;j--){
                    if(dp[j][i]-dp[j][mid-1]==len){
                        currans+=(1ll<<j);
                    }
                }
                if(currans<=k){
                    ans1=currans;
                    low=mid+1;

                }
                else high=mid-1;

            }
              low=1, high=i;
            int ans2=-1;

            while(low<=high){
                int mid=(low+high)/2;
                int len=i-mid+1;
                long long int currans=0;
                for(int j=30;j>=0;j--){
                    if(dp[j][i]-dp[j][mid-1]==len){
                        currans+=(1ll<<j);
                    }
                }
                if(currans>=k){
                    ans2=currans;
                    high=mid-1;

                }
                else low=mid+1;

            }
            // cout<<ans1<<" "<<ans2<<" "<<i<<endl;
            if(ans1!=-1)res=min(res,abs(k-ans1));
            if(ans2!=-1)res=min(res,abs(k-ans2));
        }return res;
    }
};
