class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        if(n==1) return abs(k-nums[0]);
        int maxLog=log2(n)+1;
        vector<vector<int>> table(n+1, vector<int>(maxLog));
        vector<int> log(n + 1);
        for(int i=2;i<=n;++i)
            log[i]=log[i / 2] + 1;
        for(int i=0;i<n;++i)
            table[i][0] = nums[i];
        for (int j = 1; (1 << j) <= n; ++j)
            for (int i = 0; (i + (1 << j)) <= n; ++i)
                table[i][j] = table[i][j - 1] & table[i + (1 << (j - 1))][j - 1];
        auto rangeBitwiseAnd = [&](int L, int R) {
            int j = log[R - L + 1];
            return table[L][j] & table[R - (1 << j) + 1][j];
        };

        // for(auto z:table){
        //     for(auto x:z){
        //         cout << x << " ";
        //     }
        //     cout << "\n";
        // }

        int st=0, en=-1;
        int ans=INT_MAX;
        int cur=INT_MAX;
        while(en<n){
            if(cur>k){
                en++;
                if(en==n) break;
                cur=rangeBitwiseAnd(st,en);
            }
            else if(cur<k){
                st++;
                if(st>en) cur=INT_MAX;
                else cur=rangeBitwiseAnd(st,en);
            }
            else{
                return 0;
            }
            // cout << st << " " << en << " " << cur << " " << ans << "\n";
            ans=min(ans,abs(cur-k));
        }
        return ans;
    }
};
