class Solution {
public:

    void addAndr(int n,vector<pair<int,int>>&ZeroOne){
        for(int i =0;i<32;i++){
            if(n&(1<<i))ZeroOne[i].second++;
            else{
                ZeroOne[i].first++;
            }
        }
    }

     void removeAnd(int n,vector<pair<int,int>>&ZeroOne){
        for(int i =0;i<32;i++){
            if(n&(1<<i))ZeroOne[i].second--;
            else{
                ZeroOne[i].first--;
            }
        }
    }

    int value(vector<pair<int,int>>&ZeroOne){
        int ans=0;
        for(int i =0;i<32;i++){
            if(ZeroOne[i].second && ZeroOne[i].first==0) ans+=pow(2,i);

        }
        return ans;
    }

    int minimumDifference(vector<int>& nums, int k) {
        vector<pair<int,int>>ZeroOne(32,{0,0});
        int l=0;
        int r=-1;
        int ans=INT_MAX;

        while(r < static_cast<int>(nums.size()) - 1){
            // cout<<"yes"<<endl;
            r++;
            addAndr(nums[r],ZeroOne);
            int currValue=value(ZeroOne);
            // cout<<"currValue = "<<currValue<<endl;

            ans=min(ans,abs(k-currValue));

            if(currValue>k){
                continue;
            }
            else if(currValue==k) return 0;
            else{
                while(currValue<k && l<=r){
                    // cout<<"wentInside"<<endl;
                    removeAnd(nums[l],ZeroOne);
                    l++;
                    currValue=value(ZeroOne);
                    ans=min(ans,abs(k-currValue));
                }
            }
            // cout<<currValue<<" "<<r<< " ans = "<<ans<<endl;
        }
        return ans;


    }
};
