class Solution {
public:
#define mxii 100000
#define totalnoofbits 32
int helper1[totalnoofbits][mxii];

void helper3(vector<int> &arr, int n)
{

    for (int i = 0; i < totalnoofbits; i++) {

        helper1[i][0] = ((arr[0] >> i) & 1);
        for (int j = 1; j < n; j++) {
            helper1[i][j] = ((arr[j] >> i) & 1);
            helper1[i][j] += helper1[i][j - 1];
        }
    }
}

int helper2(int l, int r)
{

    int ret = 0;

    for (int i = 0; i < totalnoofbits; i++) {
        int temp;
        if (!l){
            temp= helper1[i][r];
        }
        else{
            temp = helper1[i][r] - helper1[i][l - 1];
        }

        if (temp == r - l + 1)
            ret = (ret | (1 << i));
    }

    return ret;
}
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        helper3(nums,n);
        // for(int i=0;i<n;i++){
        //     for(int j=i;j<n;j++) cout<<i<<" "<<j<<" "<<helper2(i,j)<<endl;
        // }
        int ans=INT_MAX;
        for(auto it:nums) ans=min(ans,abs(it-k));
        // cout<<" pre"<<endl;
        // cout<<ans<<endl;
        for(int i=0;i<n;i++){
            int l=i;
            int r=n-1;
            int idx=l;
            // int pre
            // cout<<pre
            // int predif=abs(nums[l]-k);
            // cout<<endl;
            while(l<=r){

                int mid=(l+r)/2;

                int curand=helper2(i,mid);

                if(curand>k){
                    // cout<<l<<" "<<mid<<" "<<r<<" "<<curand<<endl;
                    ans=min(ans,abs(curand-k));
                    l=mid+1;
                }
                else if(curand<k){
                    // cout<<l<<" "<<mid<<" "<<r<<" "<<curand<<endl;
                    ans=min(ans,abs(curand-k));
                    r=mid-1;
                }
                else{
                    // cout<<l<<" "<<mid<<" "<<r<<" "<<curand<<endl;
                    ans=min(ans,abs(curand-k));
                    break;
                }
            }
            // ans=min(ans,predif);
        }
        return ans;
    }
};
