class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        n=len(nums)
        if(k in nums):
            return 0
        elif(n==1):
            return abs(k-nums[0])
        else:
            ans=abs(k-nums[0])
            for i in range(n):
                s=nums[i]
                ans=min(ans,abs(k-s))
                for j in range(i+1,n):
                    s&=nums[j]
                    ans=min(ans,abs(k-s))
                    if(s<=k):
                        break                        
                if(ans==0 or s>=k):
                    break
        return ans
