class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) 
    {
        int diff=2e9;
        vector<vector<int>>vec=andOrPrefixGenerator(nums);
        for(int i=0;i<nums.size();i++)
        {
            int start=i,end=nums.size()-1,mid,ans=start;
            while(start<=end)
            {
                mid=(start+end)/2;
                if(andSum(vec,i,mid)>=k)
                {
                    start=mid+1;
                    ans=mid;
                }
                else
                {
                    end=mid-1;
                }
            }
            diff=min(diff,abs(k-andSum(vec,i,ans)));
            if(ans+1<nums.size())
            {
                diff=min(diff,abs(k-andSum(vec,i,ans+1)));
            }
        }
        return diff;
    }
private:    
    vector<vector<int>>andOrPrefixGenerator(const vector<int>&vec)
    {
        vector<vector<int>>ans(1,vector<int>(32,0));
        for(auto i:vec)
        {
            ans.emplace_back(ans[ans.size()-1]);
            int pos=0;
            while(i)
            {
                ans[ans.size()-1][pos]+=i&1;
                pos++;
                i>>=1;
            }
        }
        return ans;
    }

    int andSum(const vector<vector<int>>&andOrPrefix,int l,int r)
    {
        int ans=0,curr=1;
        for(int i=0;i<andOrPrefix[0].size();i++)
        {
            if(andOrPrefix[r+1][i]-andOrPrefix[l][i]==r-l+1)
            {
                ans+=curr;
            }
            curr<<=1;
        }
        return ans;
    }
};
