class Solution {
    public int minimumDifference(int[] nums, int k) {

        int [][] nextZero = new int [nums.length][30];

        int [] current = new int [30];

        Arrays.fill(current, nums.length);

        int bit;

        for (int i = nums.length - 1; i >= 0; --i) {
            for (int j = 0; j <= 29; ++j) {
                bit = ((1 << j) & nums[i]) == 0 ? 0 : 1;

                if (bit == 0){
                    current[j] = i;
                }

                nextZero[i][j] = current[j];
            }
        }

        int result = Integer.MAX_VALUE;

        for (int num : nums) {
            result &= num;
        }

        result = Math.abs(result - k);
        int val;

        int l, r, mid;

        for (int i = 0; i < nums.length; ++i){
            if (nums[i] <= k){
                val = Math.abs(nums[i] - k);
                result = Math.min(result, val);
                continue;
            }

            l = i;
            r = nums.length - 1;
            val = getVal(nextZero, l, r);

            // System.out.println(l + " " + r + " " + val);
            if (val >= k) {
                val = Math.abs(val - k);
                result = Math.min(result, val);
                continue;
            }

            while (r - l > 1) {
                mid = (r + l) / 2;
                val = getVal(nextZero, i, mid);

                if (val >= k) {
                    l = mid;
                }else {
                    r = mid;
                }
            }

            val = getVal(nextZero, i, l);
            result = Math.min(result, Math.abs(val - k));

            val = getVal(nextZero, i, r);
            result = Math.min(result, Math.abs(val - k));
        }

        return result;
    }

    private int getVal(int [][] arr, int l, int r) {

        int result = 0;

        for (int i = 0; i < 30; ++i) {
            if (arr[l][i] > r) {
                result ^= 1 << i;
            }
        }

        return result;
    }
}
