class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        s = {}
        n = nums[0]
        s = set()
        if n > k:
            s.add(n)
        ret = abs(k - nums[0])

        for n in nums:
            ret = min(ret, abs(k - n))

            sN = set()
            if n > k:
                sN.add(n)

            for n2 in s:
                n3 = n & n2
                ret = min(ret, abs(k - n3))
                if n3 > k:
                    sN.add(n3)
            s=sN
            if ret == 0: break
        return ret
