class Solution {
public:
    void add(int a, vector<int> &bits){
        for(int i=0;i<32;i++){
            if((1<<i) & a) bits[i]++;
        }
    }

    void rem(int a, vector<int> &bits){
        for(int i=0;i<32;i++){
            if((1<<i) & a) bits[i]--;
        }
    }

    int cal(int size, vector<int> &bits){
        int ans = 0;
        for(int i=0;i<32;i++){
            if(bits[i] == size) ans+=(1<<i);
        }
        return ans;
    }

    int minimumDifference(vector<int>& a, int k) {
        int ans = INT_MAX, n = a.size();
        if(n==1) return abs(a[0] - k);
        int i=0, j=0;
        vector<int> bits(32, 0);
        while(j<n){
            add(a[j], bits);
            while(i<=j && (cal(j-i+1, bits) <= k)){
                ans = min(ans, abs(k - cal(j-i+1, bits)));
                rem(a[i], bits);
                i++;
            }
            ans = min(ans, abs(cal(j-i+1, bits) - k));
            j++;
        }
        return ans;
    }
};
