const int N = 1e5 + 10;
class Solution {
public:
    int s[N][35];
    long long query(int l, int r){
        long long res = 0;
        for(int i = 0;i<32;i++){
            if(s[r+1][i]-s[l][i]==r-l+1)res += (long long)1<<i;
        }
        return res;
    }

    int minimumDifference(vector<int>& nums, int k) {
        long long res = 1e9, n = nums.size();
        memset(s,0,sizeof s);
        for(int i = 0;i<n;i++){
            auto num = nums[i];
            for(int j = 0;j<32;j++){
                if(num>>j & 1)s[i+1][j] = s[i][j] + 1;
            }
        }
        for(int i = 0;i<n;i++){
           int l = i, r = n - 1;
            while(l<r){
                int mid = (l+r+1)>>1;
                int tres = query(i,mid);
                if(tres>=k)l = mid;
                else r = mid - 1;
            }
            res = min(res, abs(k-query(i,l)));
            if(res==0)return 0;
            // cout << i << " " << l << " " << query(i,l) << endl;
            l = i, r = n-1;
            while(l<r){
                int mid = (l+r)>>1;
                int tres = query(i,mid);
                if(tres<=k)r = mid;
                else l = mid + 1;
            }     
            // cout << i << " " << l << " " << query(i,l) << endl;
            res = min(res, abs(k-query(i,l)));
        }
        return res;
    }
};
