import java.util.*;

class SegmentTree {
    private int[] tree;
    private int n;

    public SegmentTree(List<Integer> arr) {
        n = arr.size();
        tree = new int[4 * n];
        build(arr, 0, 0, n - 1);
    }

    private void build(List<Integer> arr, int node, int start, int end) {
        if (start == end) {
            tree[node] = arr.get(start);
        } else {
            int mid = (start + end) / 2;
            build(arr, 2 * node + 1, start, mid);
            build(arr, 2 * node + 2, mid + 1, end);

            tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
        }
    }

    public int query(int l, int r) {
        return query(0, 0, n - 1, l, r);
    }

    private int query(int node, int start, int end, int l, int r) {
        if (r < start || end < l) {
            return Integer.MAX_VALUE;
        }
        if (l <= start && end <= r) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int p1 = query(2 * node + 1, start, mid, l, r);
        int p2 = query(2 * node + 2, mid + 1, end, l, r);

        return p1 & p2;
    }
}

class Solution {
    public int minimumDifference(int[] nums, int k) {
        List<Integer> arr = new ArrayList<>();
        for (int num : nums) {
            arr.add(num);
        }
        int n = arr.size();
        SegmentTree st = new SegmentTree(arr);
        int ans = Integer.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            int l = i - 1, r = n - 1, mid;

            while (r - l > 1) {
                mid = (l + r) / 2;
                if (st.query(i, mid) >= k)
                    l = mid;
                else
                    r = mid;
            }
            if (l != i - 1)
                ans = Math.min(ans, Math.abs(st.query(i, l) - k));
            ans = Math.min(ans, Math.abs(k - st.query(i, r)));
        }
        return ans;
    }
}
