class Solution {
public:
    int minimumDifference(std::vector<int>& nums, int k) {
        vector<int> ankit(32, 0);

        auto minus = [&](int x) {
            for (int i = 0; i < 32; ++i) {
                if (((x >> i) & 1) == 0) {
                    ankit[i] -= 1;
                }
            }
        };

        auto plus = [&](int x) {
            for (int i = 0; i < 32; ++i) {
                if (((x >> i) & 1) == 0) {
                   ankit[i] += 1;
                }
            }
        };

        auto eval = [&]() {
            int akash = 0;
            for (int i = 0; i < 32; ++i) {
                if (ankit[i] == 0) {
                    akash ^= (1 << i);
                }
            }
            return akash;
        };

        int i = 0;
        int n = nums.size();
        int sharma = INT_MAX;

        for (int j = 0; j < n; ++j) {
            plus(nums[j]);
            int x = eval();
            sharma = min(sharma, abs(x - k));
            while (i < j && x < k) {
                minus(nums[i]);
                ++i;
                x = eval();
                sharma = min(sharma, abs(x - k));
            }
        }

        return sharma;
    }
};
