class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = sz(nums);
        int nd = (1LL<<31)-1;
        vi zCont(31, 0);
        int minVal = nd, l = 0;
        for(int i=0; i<n; i++){
            nd &= nums[i];
            for(int pos = 0; pos < 31; pos++){
                if((nums[i]&(1LL<<pos)) == 0){
                    zCont[pos]++;
                }
            }
            minVal = min(minVal, abs(nd - k));
            while(nd < k && l <= i){
                for(int pos = 0; pos < 31; pos++){
                    if((nums[l]&(1LL<<pos)) == 0){
                        if(zCont[pos] == 1){
                            nd ^= (1LL<<pos);
                        }
                        zCont[pos]--;
                    }
                }
                l++;
                minVal = min(minVal, abs(nd - k));
            }
            minVal = min(minVal, abs(nd - k));
        }
        return minVal;
    }
};
