class Solution {
public:
    int dan[100005][20];

    int minimumDifference(vector<int>& nums, int k) {
        // kalo lebih dianggep semuanya

        for(int i = 0; i < 20; i++){
            for(int j = 0; j < nums.size(); j++){
                if(i == 0){
                    dan[j][i] = nums[j];
                }else{
                    if(j + (1 << (i-1)) < nums.size()){
                        dan[j][i] = dan[j][i-1] & dan[j + (1 << (i-1))][i-1];
                    }else{
                        dan[j][i] = dan[j][i - 1];
                    }
                }
            }
        }

        long long ans = 2e9 + 10;
        for(int i = 0; i < nums.size(); i++){
            //binary search
            int l = i;
            int r = nums.size() - 1;

            // cari yang lebih besar paling kecil
            long long ans1 = 2e9 + 10;
            // cari yang lebih kecil paling besar
            long long ans2 = -2e9 - 10;
            while(l <= r){

                int mid = (l + r) / 2; int mid2 = mid;
                int jumlah = mid - i + 1;
                long long now = (1 << 30) - 1;
                int curIndex = i;

                for(int j = 19; j >= 0; j--){
                    if(jumlah - (1 << j) >= 0){
                        now &= dan[curIndex][j];
                        curIndex += (1 << j);
                        jumlah -= (1 << j);
                    }
                }
                // cout << i << " " << mid << " " << now << endl;
                if(now < k){
                    ans2 = max(ans2, now);
                    r = mid2 - 1;
                }else{
                    ans1 = min(ans1, now);
                    l = mid2 + 1;
                }
            }
            // cout << i << " " << ans1 << " " << ans2 << endl;
            // cout << i << " " << abs(ans1 - k) << " " << abs(ans2 - k) << endl;
            ans = min(ans, min(abs(ans1 - (long long)k), abs(ans2 - (long long)k)));
        }
        return ans;
    }
};
