from typing import List

class Solution:
    def minimumDifference(self, x: List[int], y: int) -> int:

        b = [-1] * 32
        c = x[0]
        d = float('inf')
        e = len(x)
        f = 0

        for g in range(e):
            c &= x[g]

            for h in range(32):
                if (x[g] >> h) & 1:
                    b[h]=b[h]  
                else:
                    b[h] = g

            d = min(d, abs(c - y))

            while c < y and f <= g:
                if f == g:
                    if (g + 1) < e:
                        c = x[g + 1]
                    else:
                        c = 0
                else:
                    for h in range(32):
                        if b[h] == f:
                            c += (1 << h)

                d = min(d, abs(c - y))
                f += 1

            d = min(d, abs(c - y))

        return d
