class Solution {
public:

int changeand(vector<int>& bits, int k, bool x) {
    for (int i = 29; i >= 0; i--) {
        if ((k & (1 << i)) == 0) {
            if (x == 0) {
                bits[i]--;
            } else {
                bits[i]++;
            }
        }
    }

    int k1 = 0;
    for (int i = 29; i >= 0; i--) {
        if (bits[i] > 0) {
            k1 |= (1 << i);
        }
    }

    return (((1<<30)-k1)-1);
}

    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
    if (n == 0) return abs(k);

    int minDiff = abs(nums[0]-k);
    vector<int> bits(30, 0);

    int currentAnd = nums[0];
    for (int i = 0; i < 30; i++) {
        if ((currentAnd & (1 << i)) == 0) {
            bits[i]++;
        }
    }

    int left = 0;
    for (int right = 0; right < n; ++right) {
        if (right > left) {
            currentAnd = changeand(bits, nums[right], true);
            int currentDiff = abs(currentAnd - k);
            minDiff = min(minDiff, currentDiff);

        }

        while (left < right && currentAnd <k) {
            currentAnd = changeand(bits, nums[left], false);
            left++;
            int currentDiff = abs(currentAnd - k);
            minDiff = min(minDiff, currentDiff);

        }

        int currentDiff = abs(currentAnd - k);
        minDiff = min(minDiff, currentDiff);
    }

    return minDiff;
    }
};
