class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        set<int> st{-1};
        int res=INT_MAX;
        for(auto& x:nums){
            set<int> t{-1};
            for(auto& y:st){
                res=min(res,abs(k-(y&x)));
                t.insert(y&x);
                // printf("%x %x %x %d;",x,y,x&y,res);
            }
            // cout<<'\n';
            st.swap(t);
        }

        return res;
    }
};
