class Solution {
public:
    int minimumDifference(vector<int>& v, int k) {
        int n = v.size();
        vector<int> a(30,n);
        int mn = INT_MAX;
        for(int i=n-1;i>=0;i--){
            if(v[i] <= k)mn = min(mn,k-v[i]);
            else{
                mn = min(mn,v[i]-k);
                for(int j=0;j<30;j++){
                    if((v[i]&(1<<j)) && (a[j] != n)){
                        int x = v[i];
                        for(int k=0;k<30;k++){
                            if(a[k]<=a[j]){
                                x = (x&(~(1<<k)));
                            }
                        }
                        mn = min(mn,abs(k-x));
                    }
                }
            }
            for(int j=0;j<30;j++){
                if((v[i]&(1<<j)) == 0)a[j] = i;
            }
        }
        return mn;
    }
};
