class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        zeros = [0]*32

        def minus(x:int):
            for i in range(32):
                if (x>>i)&1==0:
                    zeros[i] -= 1

        def plus(x:int):
            for i in range(32):
                if (x>>i)&1==0:
                    zeros[i] += 1

        def eval():
            res = 0
            for i in range(32):
                res ^= int(zeros[i]==0)<<i
            return res


        i = 0
        n = len(nums)
        ans = inf
        for j in range(n):
            plus(nums[j])
            x = eval()
            ans = min(ans, abs(x-k))
            while i<j and x<k:
                minus(nums[i])
                i += 1
                x = eval()
                ans = min(ans, abs(x-k))

        return ans
