class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        map<int,int> mp0;
        map<int,int> mp1;
        int left=0,right=0;
        int ans=INT_MAX;
        int num=INT_MAX;
        for(right=0;right<nums.size();right++){
            num&=nums[right];
            for(int i=0;i<32;i++){
                if(((1<<i)&nums[right])>0) mp1[1<<i]++;
                else mp0[1<<i]++;
            }
            ans=min(ans,abs(k-num));
            if(num<k){
                while(left<right&&num<k){
                    for(int i=0;i<32;i++){
                        if(((1<<i)&nums[left])>0){
                            mp1[1<<i]--;
                        }
                        else{
                            mp0[1<<i]--;
                        }
                        if(mp0[1<<i]==0&&mp1[1<<i]>0){
                            num=num|(1<<i);
                        }
                    }
                    ans=min(ans,abs(k-num));
                    left++;
                }
            }
        }
        return ans;
    }
};
