#include <vector>
#include <algorithm>
#include <climits>
using namespace std;

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        vector<vector<int>> zero(n + 1, vector<int>(32, 0));

        for (int i = 0; i < n; ++i) {
            int e = nums[i];
            for (int j = 0; j < 32; ++j) {
                if ((e & (1 << j)) == 0) zero[i + 1][j] = zero[i][j] + 1;
                else zero[i + 1][j] = zero[i][j];
            }
        }

        int res = INT_MAX;

        // case 1: AND >= k
        for (int start = 0; start < n; ++start) {
            int lo = start;
            int hi = n - 1;
            while (lo < hi) {
                int mid = (lo + hi + 1) >> 1;
                int val = 0;
                for (int i = 0; i < 32; ++i) {
                    if (zero[mid + 1][i] == zero[start][i]) val ^= (1 << i);
                }
                if (val >= k) lo = mid;
                else hi = mid - 1;
            }
            int val = 0;
            for (int i = 0; i < 32; ++i) {
                if (zero[lo + 1][i] == zero[start][i]) val ^= (1 << i);
            }
            if (val >= k) res = min(res, val - k);
        }

        // case 2: AND < k
        for (int start = 0; start < n; ++start) {
            int lo = start;
            int hi = n - 1;
            while (lo < hi) {
                int mid = (lo + hi) >> 1;
                int val = 0;
                for (int i = 0; i < 32; ++i) {
                    if (zero[mid + 1][i] == zero[start][i]) val ^= (1 << i);
                }
                if (val < k) hi = mid;
                else lo = mid + 1;
            }
            int val = 0;
            for (int i = 0; i < 32; ++i) {
                if (zero[lo + 1][i] == zero[start][i]) val ^= (1 << i);
            }
            if (val < k) res = min(res, k - val);
        }

        return res;
    }
};
