class Solution {
public:
    int minimumDifference(vector<int>& v, int k) {
        vector<int> b(32,-1),z(32,-1);
        int sum=v[0],ans=INT_MAX,n=v.size();
        int j=0;
        for(int i=0;i<n;i++)
        {
            sum=sum&v[i];
            for(int j=0;j<32;j++)
            {//cout<<((v[i]>>j)&1)<<" ";
                if(((v[i]>>j)&1))
                {
                    b[j]=i;
                }
                else z[j]=i;
            }//cout<<endl;

            ans=min(ans,abs(sum-k));
            while(sum<k&&j<=i)
            {
                if(j==i) 
                {
                    if((i+1)<n) sum=v[i+1];
                    else sum=0;
                }
                else
                {
                    for(int k=0;k<32;k++)
                    {
                        if(z[k]==j) sum=sum+(1<<k);
                    }
                }
                ans=min(ans,abs(sum-k));
                j++;
            }
            //cout<<sum<<" ";
            ans=min(ans,abs(sum-k));
        }
        return ans;
    }
};
