class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int>ar(32, 0);

        auto minus = [&](int x) {
            for (int i = 0; i < 32; ++i) {
                if (((x >> i) & 1) == 0) {
                    ar[i] -= 1;
                }
            }
        };

        auto plus = [&](int x) {
            for (int i = 0; i < 32; ++i) {
                if (((x >> i) & 1) == 0) {
                    ar[i] += 1;
                }
            }
        };

        auto evaluate = [&]() {
            int res = 0;
            for (int i = 0; i < 32; ++i) {
                if (ar[i] == 0) {
                    res ^= (1 << i);
                }
            }
            return res;
        };

        int i = 0;
        int n = nums.size();
        int reslt = INT_MAX;

        for (int j = 0; j < n; ++j) {
            plus(nums[j]);
            int x = evaluate();
            reslt = min(reslt, abs(x - k));
            while (i < j && x < k) {
                minus(nums[i]);
                ++i;
                x = evaluate();
                reslt = min(reslt, abs(x - k));
            }
        }

        return reslt;
    }
};
