class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        C = Counter()

        a, b = 0, 0
        cc = []

        def push(x):
            z = 1
            while z <= x:
                if z & x:
                    C[z] += 1
                z <<= 1

        def pop(x):
            z = 1
            while z <= x:
                if z & x:
                    C[z] -= 1
                z <<= 1

        def val(a, b):
            g = [k for k, v in C.items() if v == (b-a)]
            # print(C, g)
            return sum(g) if g else 0

        while True:
            v = val(a, b)
            if b > a:
                if k == v:
                    return 0
                cc.append(abs(k-v))
            if k < v or a == b:
                if b == len(nums):
                    break
                push(nums[b])
                b += 1
            elif a < b:
                pop(nums[a])
                a += 1


        return min(cc)
