class Solution {
    int k;
    Map<String, Integer> dp;
    public int minimumDifference(int[] nums, int k) {
        this.k = k;
        dp = new HashMap<>();
        return dfs(Integer.MAX_VALUE, nums, 0);
    }
    private int dfs(int prev, int[] nums, int index) {
        String key = prev + ":" + index;
        Integer v = dp.get(key);
        if (v != null) {
            return v;
        }
        int m = nums.length, sum = prev & nums[index];
        int diff1 = Math.abs(sum - k), res = diff1;
        if (sum > k && index < m - 1) {
            int next = dfs(sum, nums,  index + 1);
            res = Math.min(res, next);
        }
        int diff2 = Math.abs(nums[index] - k);
        res = Math.min(res, diff2);
        if (nums[index] > k && index < m - 1) {
            int next = dfs(nums[index], nums,  index + 1);
            res = Math.min(res, next);
        }
        if (index < m - 1) {
            int next = dfs(Integer.MAX_VALUE, nums, index + 1);
            res = Math.min(res, next);
        }
        dp.put(key, res);
        return res;
    }
}
