class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        vector<int> bit(32,0);
        auto add=[&](int a){
            for(int i=0;i<31;i++){
                if(((a>>i)&1)==0){
                    bit[i]++;
                }
            }
        };
        auto remove = [&](int a){
            for(int i=0;i<31;i++){
                if(((a>>i)&1)==0){
                    bit[i]--;
                }
            }
        };
        auto solve = [&](){
            int xr=0;
            for(int i=0;i<31;i++){
                if(bit[i]==0){
                    xr^=(1<<i);
                }
            }
            return xr;
        };
        int n=nums.size();
        int tmp=0;
        int j=0;
        int mx=INT_MAX;
        for(int i=0;i<n;i++){
            add(nums[i]);
            int tmp=solve();
            mx=min(mx,abs(tmp-k));
            while(j<i&&tmp<k){
                remove(nums[j]);
                j++;
                tmp=solve();
                mx=min(mx,abs(tmp-k));
            }
        }
        return mx;
    }
};
