class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int ans = abs(nums[0] - k);
        unordered_set<int> cand = {nums[0]};
        int n = nums.size();
        for (int i=0; i<n; i++) {
            unordered_set<int> tmp {nums[i]};
            for (int x: cand) {
                tmp.insert(x & nums[i]);
            }
            for (int x: tmp) ans = min(ans, abs(x-k));
            cand = tmp;
        }
        return ans;
    }
};
