import java.util.*;

class Solution {
    public int minimumDifference(int[] a, int k) {
        int ans = Integer.MAX_VALUE;
        int i = 0;
        while(i < a.length) {
            if(a[i] == k) {
                return 0;
            }
            else if(a[i] < k) {
                ans = Math.min(ans, k - a[i]);
                i++;
            }
            else {
                int j = i;
                List<Integer> bigger = new ArrayList<>();
                while(j < a.length && a[j] > k) {
                    bigger.add(a[j]);
                    j++;
                }
                ans = Math.min(ans, compute(bigger, k));
                i = j;
            }
        }
        return ans;
    }
    private int compute(List<Integer> list, int k) {
        int n = list.size();
        int[][] f = new int[32][n];
        for(int i = 0; i < n; i++) {
            for(int j = 0; j < 32; j++) {
                if(((1l << j) & list.get(i)) != 0) {
                    f[j][i] = (i > 0 ? f[j][i - 1] : 0) + 1;
                }
                else {
                    f[j][i] = (i > 0 ? f[j][i - 1] : 0);
                }
            }
        }
        int ans = Integer.MAX_VALUE;
        for(int i = 0; i < n; i++) {
            ans = Math.min(ans, helper(f, i, k));
        }
        return ans;
    }
    private int helper(int[][] f, int idx, int k) {
        int l = 0, r = idx;
        while(l < r - 1) {
            int mid = l + (r - l) / 2;
            int v = getVal(f, mid, idx);
            if(v < k) {
                l = mid;
            }
            else {
                r = mid;
            }
        }
        return Math.min(Math.abs(k - getVal(f, l, idx)), Math.abs(k - getVal(f, r, idx)));
    }
    private int getVal(int[][] f, int l, int r) {
        int v = 0;
        for(int i = 0; i < f.length; i++) {
            if(f[i][r] - (l > 0 ? f[i][l - 1] : 0) == r - l + 1) {
                v |= (1 << i);
            }
        }
        return v;
    }
    public static void main(String[] args) {
        int[] a = new int[]{21,45,56,61,14};
        Solution solution = new Solution();
        solution.minimumDifference(a,12);
    }
}
