#include <iostream>
#include <vector>
#include <climits>
using namespace std;

class SegmentTree {
private:
    vector<int> tree;
    int n;

    void buildTree(vector<int>& arr, int node, int start, int end) {
        if (start == end) {
            tree[node] = arr[start];
        } else {
            int mid = (start + end) / 2;
            buildTree(arr, 2 * node + 1, start, mid);
            buildTree(arr, 2 * node + 2, mid + 1, end);
            tree[node] = tree[2 * node + 1] & tree[2 * node + 2];
        }
    }

    int rangeQuery(int node, int start, int end, int l, int r) {
        if (r < start || end < l) {
            return INT_MAX;
        }
        if (l <= start && end <= r) {
            return tree[node];
        }
        int mid = (start + end) / 2;
        int leftResult = rangeQuery(2 * node + 1, start, mid, l, r);
        int rightResult = rangeQuery(2 * node + 2, mid + 1, end, l, r);
        return leftResult & rightResult;
    }

public:
    SegmentTree(vector<int>& arr) {
        n = arr.size();
        tree.resize(4 * n);
        buildTree(arr, 0, 0, n - 1);
    }

    int query(int l, int r) {
        return rangeQuery(0, 0, n - 1, l, r);
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        SegmentTree segTree(nums);
        int minDiff = INT_MAX;

        for (int i = 0; i < n; ++i) {
            int left = i - 1;
            int right = n - 1;

            while (right - left > 1) {
                int mid = (left + right) / 2;
                if (segTree.query(i, mid) >= k) {
                    left = mid;
                } else {
                    right = mid;
                }
            }

            if (left != i - 1) {
                minDiff = min(minDiff, abs(segTree.query(i, left) - k));
            }
            minDiff = min(minDiff, abs(k - segTree.query(i, right)));
        }

        return minDiff;
    }
};
