    // minus
        void subtract(int x, vector<int> &zeroes){
            for(int i=0; i<32; i++)
            {
                if(((x>>i) & 1) == 0)
                {
                    zeroes[i] -= 1;
                }
            }
        }


        // for plus addition
        void add(int x, vector<int> &zeroes){
            for(int i=0; i<32; i++)
            {
                if(((x>>i)&1) == 0)
                {
                    zeroes[i]+= 1;
                }
            }
        }


       int eval(vector<int> &zeroes)
       {
           int result = 0;
           for(int i=0; i<32; i++)
           {
               if(zeroes[i] == 0)
               {
                   result = result ^ (1<<i);
               }
           }
           return result;
       }


    int minimumDifference(vector<int>& nums, int k) {
       vector<int> zeroes(32, 0);

        int i=0;
        int n = nums.size();
        int result = INT_MAX;

        for(int j=0; j<n; j++)
        {
            add(nums[j], zeroes);
            int x = eval(zeroes);
            result = min(result, abs(x-k));

            while(i<j && x<k)
            {
                subtract(nums[i], zeroes);
                i++;
                x = eval(zeroes);
                result = min(result, abs(x-k));
            }
        }
        return result;
    }
};

// T.C.:O(n * 32 * (j-i)))
