class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        vector<vector<int>> bit(n,vector<int>(32,0));
        for(int i=0;i<n;i++){
            for(int j=0;j<31;j++){
                if(((1LL<<j)&nums[i])!=0)
                    bit[i][j]=1;
            }
        }
        for(int i=1;i<n;i++){
            for(int j=0;j<31;j++)
            {
                bit[i][j]+=bit[i-1][j];
            }
        }
        // for(int i=0;i<n;i++){
        //     for(int j=0;j<31;j++)
        //     {
        //         cout<<bit[i][j]<<" ";
        //     }
        //     cout<<endl;
        // }
     //   cout<<endl;
        int ans=1e9;
        for(int i=0;i<n;i++){
            if(nums[i]<=k){
                int diff=k-nums[i];
                ans=min(ans,diff);
                continue;
            }
            int st=i,en=n-1;
            int res=i,val1=nums[i];
            while(st<=en)
            {
                int mid=(st+en)/2;
                vector<int>bits=bit[mid];

                if(i>0){
                    for(int j=0;j<32;j++){
                        bits[j]-=bit[i-1][j];
                    }
                }
                  // for(auto &x:bits)
                  //   cout<<x<<" ";
                  //   cout<<endl;
                int val=0;
                for(int j=0;j<31;j++){
                    if(bits[j]==(mid-i+1))
                        val+=(1LL<<j);
                }
             //   cout<<val<<" "<<mid<<endl;
                if(val>=k){
                    res=mid;
                    val1=val;
                    st=mid+1;
                }else{
                    en=mid-1;
                }

            }
            int diff1=abs(val1-k);
            int diff2=1e9;
            if((res+1)<n)
            {
                 vector<int>bits=bit[res+1];
                if(i>0){
                     for(int j=0;j<32;j++){
                        bits[j]-=bit[i-1][j];
                    }
                }
                 int val=0;
                for(int j=0;j<31;j++){
                    if(bits[j]==((res+1)-i+1))
                        val+=(1LL<<j);
                }
                diff2=abs(val-k);

            }

            ans=min(ans,min(diff1,diff2));
        }
        return ans;
    }
};
