#include<bits/stdc++.h>
using namespace std;

struct ST {
    int s, e, val;
    ST *l, *r;
    ST(int S, int E, vector<int>& a):s(S),e(E) {
        if(s == e) val = a[s];
        else {
            int m = (s+e)/2;
            l = new ST(s, m, a);
            r = new ST(m+1, e, a);
            val = l->val & r->val;
        }
    }
    int get(int S, int E) {
        if(E<s||e<S||E<S) return -1;
        else if(S<=s&&e<=E) return val;
        else return l->get(S,E) & r->get(S,E);
    }
};

class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        long long mn = 1e18;
        ST* st = new ST(0, n-1, nums);
        for(int i=0;i<n;i++) {
            int s = i, e = n-1;
            while(s<e) {
                int mid = i + (s-i + e-i)/ 2;
                if(st->get(i, mid) <= k) e = mid;
                else s = mid + 1;
            }
            long long res = st->get(i, e);
            if(mn > abs(res - k)) {
                mn = abs(res - k);
            }
            if(e-1 >= i) {
                res = st->get(i, e-1);
                mn = min(mn, abs(res - k));
            }
        }
        return mn;
    }
};
