def val(w,l,r):
    n = r-l+1
    res = 0
    for i in range(32):
        if w[i] == n:
            res += (1 << i)
    return res 

def add(w,x):
    for i in range(32):
        if (1<<i) & x != 0:
            w[i] += 1

def remove(w,x):
    for i in range(32):
        if (1<<i) & x != 0:
            w[i] -= 1

class Solution:
    def minimumDifference(self, nums: List[int], k: int) -> int:
        n = len(nums)
        res = float('inf')
        w = [0]*32
        l = 0
        for r in range(n):
            add(w,nums[r])
            v = val(w,l,r)
            while l < r and v < k:
                res = min( res ,  abs(k - v) )
                remove(w, nums[l])
                l += 1
                v = val(w,l,r)

            res = min(res, abs(k-v))
        return res
