class Solution {

    public class SegmentTree {
        int[] tree;
        int n;

        public SegmentTree(int[] arr) {
            n = arr.length;
            tree = new int[4 * n];
            build(arr, 0, 0, n - 1);
        }

        public void build(int[] arr, int node, int start, int end) {
            if (start == end) {
                tree[node] = arr[start];
            }
            else {
                int mid = start + (end- start) / 2;
                build(arr, 2 * node + 1, start, mid);
                build(arr, 2 * node + 2, mid + 1, end);
                tree[node]
                    = tree[2 * node + 1] & tree[2 * node + 2];
            }
        }

        public int query(int l, int r) {
            return query(0, 0, n - 1, l, r);
        }

        public int query(int node, int start, int end, int l,
                         int r) {
            if (r < start || end < l) {
                return Integer
                    .MAX_VALUE;
            }
            if (l <= start && end <= r) {
                return tree[node];
            }
            int mid = (start + end) / 2;
            int p1 = query(2 * node + 1, start, mid, l, r);
            int p2 = query(2 * node + 2, mid + 1, end, l, r);
            return p1 & p2;
        }
    }

    public int minimumDifference(int[] nums, int k) {
        int n = nums.length;
        SegmentTree st = new SegmentTree(nums);
        int ans = Integer.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            int low = i - 1, high = n - 1;
            while (high - low > 1) {
                int mid = low + (high-low) / 2;
                if (st.query(i, mid) >= k)
                    low = mid;
                else
                    high = mid;
            }
            if (low != i - 1)
                ans = Math.min(
                    ans, Math.abs(st.query(i, low) - k));
            ans = Math.min(ans,
                           Math.abs(k - st.query(i, high)));
        }
        return ans;
    }
}
