class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n = nums.size();
        int close = INT_MAX;
        map<int,int> a,b;
        for(int i=0;i<n;i++) {
            b.clear();
            for(auto &itr : a) {
                b[itr.first&nums[i]]++;
            }
            b[nums[i]]++;
            swap(a,b);
            for(auto &itr : a) {
                close = min(close, abs(itr.first-k));
            }
        }
        return close;

    }
};
