class Solution {
public:
    int minimumDifference(vector<int>& a, int k) {
        int n = a.size();
        int cv = a[0];
        int bd = abs(cv - k);

        unordered_map<int, int> bc;
        ibc(bc, a[0]);

        int l = 0, r = 0;
        while (r < n) {
            if (cv > k || l > r) {
                r++;
                if (r >= n) {
                    break;
                }
                anb(bc, a[r]);
            } else {
                rmb(bc, a[l]);
                l++;
            }

            cv = ccv(bc, r - l + 1);
            if (l <= r) {
                bd = min(bd, abs(cv - k));
            }
        }

        return bd;
    }

private:
    void ibc(unordered_map<int, int>& bc, int num) {
        for (int j = 0; j < 32; j++) {
            if (num & (1 << j)) {
                bc[j]++;
            }
        }
    }

    void anb(unordered_map<int, int>& bc, int num) {
        for (int j = 0; j < 32; j++) {
            if (num & (1 << j)) {
                bc[j]++;
            }
        }
    }

    void rmb(unordered_map<int, int>& bc, int num) {
        for (int j = 0; j < 32; j++) {
            if (num & (1 << j)) {
                bc[j]--;
            }
        }
    }

    int ccv(const unordered_map<int, int>& bc, int ws) {
        int cv = 0;
        for (const auto& it : bc) {
            if (it.second == ws) {
                cv |= (1 << it.first);
            }
        }
        return cv;
    }
};
