#define INF INT32_MAX
class SGTree{
    vector<int>seg;
    public:
    SGTree(int n){
        seg.resize(4*n+1);
    }
    void build(int ind,int low,int high,int arr[]){
        if(low==high){
            seg[ind]=arr[low];
            return;
        }
        int mid=(low+high)/2;
        build(2*ind+1,low,mid,arr);
        build(2*ind+2,mid+1,high,arr);
        seg[ind]=(seg[2*ind+1]&seg[2*ind+2]);
    }
    int query(int ind,int low,int high,int l,int r){
        //no overlap
        if(r<low||high<l) return INF;

        //complete overlap
        if(low>=l&&high<=r) return seg[ind];

        //partial overlap
        int mid=(low+high)/2;
        int left=query(2*ind+1,low,mid,l,r);
        int right=query(2*ind+2,mid+1,high,l,r);
        return (left&right); 
    }

    void update(int ind,int low,int high,int i,int val){
        if(low==high){
            seg[ind]=val;
            return;
        }
        int mid=(low+high)/2;
        if(i<=mid){
            update(2*ind+1,low,mid,i,val);
        }
        else
            update(2*ind+2,mid+1,high,i,val);

        seg[ind]=(seg[2*ind+1]&seg[2*ind+2]);

    }
};




class Solution {
public:
    int minimumDifference(vector<int>& nums, int k) {
        int n=nums.size();
        int arr[n];
        for(int i=0;i<n;i++)
             arr[i]=nums[i];
        SGTree sg(n);
        sg.build(0,0,n-1,arr);
        int res=INF;
        int prev=INF;
        for(int i=0;i<n;i++){
            int l=i-1,h=n-1;
            while(h-l>1){
                int mid=(l+h)/2;
                if(sg.query(0,0,n-1,i,mid)>=k){
                    l=mid;
                }
                else
                    h=mid;
            }
            if(l!=i-1)
                res=min(res,abs(sg.query(0,0,n-1,i,l)-k));
            res=min(res,abs(k-sg.query(0,0,n-1,i,h)));

        }
        return res;
    }
};
