class Solution {
private:
    int pre[31][100001];
public:

void pre_cal(vector<int> arr, int n)
{

    for (int i = 0; i < 31; i++) {

        pre[i][0] = ((arr[0] >> i) & 1);
        for (int j = 1; j < n; j++) {
            pre[i][j] = ((arr[j] >> i) & 1);
            pre[i][j] += pre[i][j - 1];
        }
    }
}

int rangeAnd(int l, int r)
{

    int ans = 0;


    for (int i = 0; i < 31; i++) {
        int x;
        if (l == 0)
            x = pre[i][r];
        else
            x = pre[i][r] - pre[i][l - 1];

        if (x == r - l + 1)
            ans = (ans | (1 << i));
    }

    return ans;
}

    int minimumDifference(vector<int>& a, int k) {
        int n = (int)a.size();
        pre_cal(a, n);
        int ans = 1e9;
        for(int i = 0; i < n;i++){
            int l = i, r = n - 1;
            int cl = -1e9;
            while(l <= r){
                int mid = (l + r) / 2;
                int x = rangeAnd(i, mid);
                if(x > k){
                    l = mid + 1;
                }else{
                    cl = x;
                    r = mid - 1;
                }
            }
            if(cl != -1e9) ans = min(ans, k - cl);
            l = i, r = n - 1;
            cl = 2e9;
            while(l <= r){
                int mid = (l + r) / 2;
                int x = rangeAnd(i, mid);
                if(x < k){
                    r = mid - 1;
                }else{
                    cl = x;
                    l = mid + 1;
                }
            }
            if(cl != 2e9) ans = min(ans, cl - k);
        }
        return ans;
    }
};
